var form=$(".steps-validation").show();
$(".steps-validation").steps({
    headerTag:"h6",
    bodyTag:"fieldset",
    transitionEffect:"fade",
    titleTemplate:'<span class="step">#index#</span> #title#',
    labels:{
        finish:"Submit"
    },
    onStepChanging:function(e,t,i){
        
        if(t == 0 && t<i){
            var contype = $('.checkie:checked').attr('val');
            if(contype == 'company'){
                $('#profileCompany').data('formValidation').validate();
                if($('#errorfieldProfileCompany').val() == 0){
                    return true;
                }
                else return false;
            }
            else if(contype == 'personal'){
                $('#profilePersonal').data('formValidation').validate();
                if($('#errorfieldProfilePersonal').val() == 0){
                    return true;
                }
                else return false;
            }
            
        }
        
        if(t == 1 && t<i){
            $('#requirements').data('formValidation').validate();
            if($('#errorfieldReq').val() == 0){
                return true;
            }
            else return false;
        }

        if(t == 2 && t<i){
            $('#credentials').data('formValidation').validate();
            if($('#errorfieldCred').val() == 0){
                return true;
            }
            else return false;
        }

        if(t>i){
            return true
        }
    },
    // onFinishing:function(e,t){
    // },
    // onFinished:function(e,t){
    //     alert("Submitted!")
    // }
})
